import { Component } from '@angular/core';
import servicosJson from 'src/assets/servicos.json';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { Observable } from 'rxjs';
import { map, shareReplay } from 'rxjs/operators';

@Component({
  selector: 'app-link-servicos',
  templateUrl: './link-servicos.component.html',
  styleUrls: ['./link-servicos.component.css']
})
export class LinkServicosComponent {
  servicos = servicosJson.servicos;
  isHandset$: Observable<boolean> = this.breakpointObserver.observe('(max-width: 767px)')
    .pipe(
      map(result => result.matches),
      shareReplay()
    );

  // Novo campo para rastrear se o painel está aberto
  public isPanelOpen: boolean = false;

  constructor(private breakpointObserver: BreakpointObserver) { }

  // Nova função para alternar o valor de isPanelOpen
  togglePanel() {
    this.isPanelOpen = !this.isPanelOpen;
  }
}
<!-- link-servicos.component.html -->
<div class="servicos-container link-servicos" *ngIf="isHandset$ | async; else desktopVersion">
  <mat-accordion class="my-accordion">
    <mat-expansion-panel (opened)="togglePanel()" (closed)="togglePanel()">
      <mat-expansion-panel-header class="my-header">
        <p class="servico-nome servico-link">Clique aqui e veja nossos serviços</p>
      </mat-expansion-panel-header>
      <hr *ngIf="isPanelOpen" class="link-divider first-divider" /> <!-- Adicione esta linha -->
      <ng-container *ngFor="let servico of servicos; let i = index">
        <a [routerLink]="[servico.route]" class="servico-link servico-item" [ngClass]="{'first-link': i === 0 && isPanelOpen}">
          <p class="servico-nome">{{servico.nome}}</p>
        </a>
        <hr class="link-divider" />
        <ng-container *ngIf="i === 4">
          <div class="hr-container">
            <hr />
          </div>
        </ng-container>
      </ng-container>
    </mat-expansion-panel>
  </mat-accordion>
</div>

<ng-template #desktopVersion>
  <!-- Sua versão desktop aqui -->
  <!-- link-servicos.component.html -->
  <div class="servicos-container link-servicos">
    <ng-container *ngFor="let servico of servicos; let i = index">
      <a [routerLink]="[servico.route]" class="servico-link servico-item">
        <div class="">
          <p class="servico-nome">{{servico.nome}}</p>
        </div>
        <hr class="link-divider" />
      </a>
      <ng-container *ngIf="i === 4">
        <div class="hr-container">
          <hr />
        </div>
      </ng-container>
    </ng-container>
  </div>
</ng-template>
